CREATE TABLE `jobs` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(255) NOT NULL COMMENT 'Name',
  `media_id` bigint(20) unsigned DEFAULT NULL COMMENT 'Media ID',
  `job_category_id` bigint(20) unsigned NOT NULL COMMENT 'Job Category ID',
  `job_type_id` bigint(20) unsigned NOT NULL COMMENT 'Job Type ID',
  `description` text COMMENT 'Description',
  `detail` text COMMENT 'Detail',
  `business_skill` varchar(1000) DEFAULT NULL COMMENT 'Business Skill',
  `knowledge` varchar(1000) DEFAULT NULL COMMENT 'Knowledge',
  `location` varchar(1000) DEFAULT NULL COMMENT 'Location',
  `activity` varchar(1000) DEFAULT NULL COMMENT 'Activity',
  `academic_degree_doctor` tinyint(1) DEFAULT '0' COMMENT 'Academic Degree Doctorate',
  `academic_degree_master` tinyint(1) DEFAULT '0' COMMENT 'Academic Degree Master',
  `academic_degree_professional` tinyint(1) DEFAULT '0' COMMENT 'Academic Degree Professional',
  `academic_degree_bachelor` tinyint(1) DEFAULT '0' COMMENT 'Academic Degree Bachelor',
  `salary_statistic_group` varchar(255) DEFAULT NULL COMMENT 'Salary Statistic Group',
  `salary_range_first_year` decimal(15,2) DEFAULT NULL COMMENT 'Salary Range First Year',
  `salary_range_average` decimal(15,2) DEFAULT NULL COMMENT 'Salary Range Average',
  `salary_range_remarks` varchar(1000) DEFAULT NULL COMMENT 'Salary Range Memo',
  `restriction` varchar(1000) DEFAULT NULL COMMENT 'Restriction',
  `estimated_total_workers` varchar(255) DEFAULT NULL COMMENT 'Estimated Total Workers',
  `remarks` varchar(1000) DEFAULT NULL COMMENT 'Memo',
  `url` varchar(255) DEFAULT NULL COMMENT 'URL',
  `seo_description` varchar(1000) DEFAULT NULL COMMENT 'SEO Description',
  `seo_keywords` varchar(255) DEFAULT NULL COMMENT 'SEO Keywords',
  `sort_order` int(11) NOT NULL DEFAULT '0' COMMENT 'Sort Order',
  `publish_status` tinyint(1) NOT NULL COMMENT 'Published Status',
  `version` int(11) NOT NULL DEFAULT '0' COMMENT 'Version',
  `created_by` bigint(20) unsigned NOT NULL COMMENT 'Created By',
  `created` datetime NOT NULL COMMENT 'Created At',
  `modified` datetime NOT NULL COMMENT 'Modified At',
  `deleted` datetime DEFAULT NULL COMMENT 'Deleted At',
  PRIMARY KEY (`id`),
  KEY `IDX_ACADEMIC_DEGREE_BACHELOR` (`academic_degree_bachelor`),
  KEY `IDX_ACADEMIC_DEGREE_DOCTOR` (`academic_degree_doctor`),
  KEY `IDX_ACADEMIC_DEGREE_MASTER` (`academic_degree_master`),
  KEY `IDX_ACADEMIC_DEGREE_PROFESSIONAL` (`academic_degree_professional`),
  KEY `IDX_JOB_CATEGORY_ID` (`job_category_id`),
  KEY `IDX_JOB_TYPE_ID` (`job_type_id`),
  KEY `IDX_MEDIA_ID` (`media_id`),
  KEY `IDX_NAME` (`name`(191)),
  KEY `IDX_PUBLISH_STATUS` (`publish_status`),
  KEY `IDX_SORT_ORDER` (`sort_order`),
  KEY `IDX_VERSION` (`version`)
) ENGINE=InnoDB AUTO_INCREMENT=295 DEFAULT CHARSET=utf8mb4;

